import { Component, OnInit } from '@angular/core';
import { IProduct } from './models/products';
import { ProductService } from './services/product.service';
import { Observable, tap } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent implements OnInit {
  title = 'my-app';
  loading: boolean = true;
  products$: Observable<IProduct[]>;
  term: string = '';

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.loading = true;
    this.products$ = this.productService
      .getAll()
      .pipe(tap(() => (this.loading = false)));
  }
}
<app-global-error></app-global-error>
<div class="max-w-2xl container mx-auto pt-5">
  <h1 class="font-bold mb-2 text-center text-lg">{{ title | titlecase }}</h1>
  <input
    type="text"
    class="mb-2 border py-2 px-4 w-full"
    placeholder="Filter products..."
    [(ngModel)]="term"
  />
  <p *ngIf="loading" class="text-center text-lg">Loading ...</p>

  <div *ngIf="products$ | async as products">
    <app-product
      *ngFor="let product of products | filterProducts : term; let i = index"
      [product]="product"
    ></app-product>
  </div>
  <app-modal></app-modal>
</div>
