import { Component, OnInit } from '@angular/core';
import { Observable, tap } from 'rxjs';
import { IProduct } from 'src/app/models/products';
import { ModalService } from 'src/app/services/modal.service';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-product-page',
  templateUrl: './product-page.component.html',
  styleUrls: ['./product-page.component.scss'],
})
export class ProductPageComponent implements OnInit {
  title = 'my-app';
  loading: boolean = true;
  products$: Observable<IProduct[]>;
  term: string = '';

  constructor(
    private productService: ProductService,
    public modalService: ModalService
  ) {}

  ngOnInit(): void {
    this.loading = true;
    this.products$ = this.productService
      .getAll()
      .pipe(tap(() => (this.loading = false)));
  }
}
<div class="max-w-3xl container mx-auto pt-5">
  <h1 class="font-bold mb-2 text-center text-lg">{{ title | titlecase }}</h1>
  <input
    type="text"
    class="mb-2 border py-2 px-4 w-full"
    placeholder="Filter products..."
    [(ngModel)]="term"
  />
  <p *ngIf="loading" class="text-center text-lg">Loading ...</p>

  <div *ngIf="products$ | async as products" class="grid grid-cols-3 gap-2">
    <app-product
      *ngFor="let product of products | filterProducts : term; let i = index"
      [product]="product"
    ></app-product>
  </div>

  <app-modal title="Create new product" *ngIf="modalService.isVisible$ | async">
    <app-create-product></app-create-product>
  </app-modal>

  <button
    class="rounded-full bg-red-700 text-white fixed right-5 bottom-5 py-2 px-4"
    (click)="modalService.open()"
  >
    +
  </button>
</div>
